import axios from 'axios';
import React from 'react';
import { Link } from 'react-router-dom';
import makeToast from "../Toaster/toaster";
import io from "socket.io-client";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';


// new 

import { withRouter } from "react-router";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import Logo from '../images/logo.png'

const DashboardPage = (props) => {
    const chatroomNameRef = React.createRef();
    const newRoomIdRef = React.createRef();
    const joinRoomIdRef = React.createRef();
    const [chatrooms, setChatrooms] = React.useState([]);
    const SetupSocket = () => {
        const socket = io("http://192.168.216.63:8000", {
            query: {
                token: localStorage.getItem("U_Token"),
            }
        });
        if (socket) {
            socket.on("disconnect", () => {
                setTimeout(SetupSocket, 3000);
            });
        }

        if (socket) {
            socket.on("connect", () => {
                console.log("Socket Connected!")
            });
        }

    }
    SetupSocket();

    // Serives Routing Funtions 

    const goWithdraw = ()=>{

    }

    const goDeposit = ()=>{

        props.history.push("/deposit");

    }


    const logout = () => {
        localStorage.removeItem("U_Token");
    }
    React.useEffect(() => {
        getChatrooms();
        //eslint-disable-next-line
    }, []);

    const joinChatRoom = () => {
        const joinRoomId = joinRoomIdRef.current.value;
        props.history.push("/chatroom/" + "random" + "/" + joinRoomId);
        window.location.reload()
    }

    return (
        <>

        <div class="container">
          <div className="row"></div>

          <div className="row main-box">
            <div className="col-sm-0.5"></div>

            <div className="col-sm-11 inner-box">

                <div className="row logo-lane">
                <div className="col-sm-4 logo-lane"></div>
                <div className="col-sm-4 logo-lane">
                <img src={Logo} className="shift-logo" alt="" />
                </div>
                <div className="col-sm-4 logo-lane"></div>
                </div>

                <div className="row one-row-gap"><span className="Tagline-Top Tagline"> Services</span></div>



                    <div className="row ser-button">
                    <div className="col-sm-5 ser-button">
                        <button className="left-sbutton">Account Info</button>
                    </div>
                    <div className="col-sm-2 ser-button"></div>
                    <div className="col-sm-5 ser-button">
                    <button className="right-sbutton" onClick={goWithdraw}>Withdraw</button>
                    </div>
                    </div>

                    <div className="row ser-button">
                    <div className="col-sm-5 ser-button">
                        <button className="left-sbutton">Change ATM Pin</button>
                    </div>
                    <div className="col-sm-2 ser-button"></div>
                    <div className="col-sm-5 ser-button">
                    <button className="right-sbutton" onClick ={goDeposit}>Deposit</button>
                    </div>
                    </div>

                    <div className="row ser-button">
                    <div className="col-sm-5 ser-button">
                        <button className="left-sbutton">Chnage Phone No</button>
                    </div>
                    <div className="col-sm-2 ser-button"></div>
                    <div className="col-sm-5 ser-button">
                    <button className="right-sbutton">Balance Enquery</button>
                    </div>
                    </div>

            </div>

            <div className="col-sm-0.5"></div>

          </div>
        </div>

    </>

    )
};

export default DashboardPage;
