import React from 'react';
import axios from "axios";
import makeToast from "../Toaster/toaster";
import { withRouter } from 'react-router';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import {FlashOn} from "@material-ui/icons";

function LoginPage(props) {
    const cardnoRef = React.createRef();
    const pinRef = React.createRef();

    const loginUser = () => {
        const cardno = cardnoRef.current.value;
        const pin = pinRef.current.value;

        axios.post("http://localhost:8000/user/login", {
            cardno,
            pin,
        })
            .then((response) => {

                makeToast("success", response.data.message);
                localStorage.setItem("U_Token", response.data.token);
                props.history.push("/servies");

            }).catch((err) => {
                if (
                    err &&
                    err.response &&
                    err.response.data &&
                    err.response.data.message


                ) { makeToast("error", err.response.data.message); }
            });
    };
    return (
        <div>

        <div className="background">

        <div className="form-group">
                        <label className="form-label" htmlFor="cardno">Card Number</label>
                        <input
                        className="form-control"
                            type="text"
                            name="cardno"
                            placeholder="Your Card Number"
                            ref={cardnoRef}
                        />
                    </div>
                    <div className="form-group">
                        <label className="form-label" htmlFor="pin">Your ATM Pin</label>
                        <input
                            className="form-control"
                            type="text"
                            name="pin"
                            placeholder="XXXX"
                            ref={pinRef}
                        />
                    </div>


        </div>


        </div>
    )
}

export default withRouter(LoginPage);
