import React from "react";
import axios from "axios";
import makeToast from "../Toaster/toaster";
import { withRouter } from "react-router";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import { FlashOn } from "@material-ui/icons";
import Logo from '../images/logo.png'

function CheckBalancePage(props) {
  const cardnoRef = React.createRef();
  const pinRef = React.createRef();

  const getbalance = () => {
    const cardno = cardnoRef.current.value;
    const pin = pinRef.current.value;

    axios
      .post("http://localhost:8000/user/login", {
        cardno,
        pin,
      })
      .then((response) => {
        makeToast("success", response.data.message);
        localStorage.setItem("U_Token", response.data.token);
        props.history.push("/servies");
      })
      .catch((err) => {
        if (
          err &&
          err.response &&
          err.response.data &&
          err.response.data.message
        ) {
          makeToast("error", err.response.data.message);
        }
      });
  };
  return (
    <>
      
        <div class="container">
          <div className="row"></div>

          <div className="row main-box">
            <div className="col-sm-0.5"></div>

            <div className="col-sm-11 inner-box">
                
                <div className="row logo-lane">
                <div className="col-sm-4 logo-lane"></div>
                <div className="col-sm-4 logo-lane">
                <img src={Logo} className="shift-logo" alt="" />
                </div>
                <div className="col-sm-4 logo-lane"></div>
                </div>

                <div className="row one-row-gap">
                  <span className="Tagline">Balance Enquery</span>
                </div>

                    <div className="row ser-button">
                    <div className="col-sm-5 ser-button">
                        <span className="input-label">
                          Available Balance 
                        </span>
                    </div>
                    <div className="col-sm-2 ser-button"></div>
                    <div className="col-sm-5 ser-button">
                    <button className="right-sbutton">Services</button>
                    </div>
                    </div>

                    <div className="row ser-button">
                    <div className="col-sm-5 ser-button">
                    <span className="input-label">
                          Available Balance 
                        </span>
                    <label className="input-label Tagline">
                          RS : 5000000
                        </label>
                    </div>
                    <div className="col-sm-2 ser-button"></div>
                    <div className="col-sm-5 ser-button">
                    <button className="right-sbutton">Withdraw</button>
                    </div>
                    </div>

                    <div className="row ser-button">
                    <div className="col-sm-5 ser-button">
                        
                    </div>
                    <div className="col-sm-2 ser-button"></div>
                    <div className="col-sm-5 ser-button">
                    <button className="right-sbutton">Exit</button>
                    </div>
                    </div>

            </div>

            <div className="col-sm-0.5"></div>

          </div>
        </div>
      
    </>
  );
}

export default withRouter(CheckBalancePage);
