import React from "react";
import axios from "axios";
import makeToast from "../Toaster/toaster";
import { withRouter } from "react-router";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min";
import { FlashOn } from "@material-ui/icons";

function LoginPage(props) {
  const cardnoRef = React.createRef();
  const pinRef = React.createRef();

  const loginUser = () => {
    const cardno = cardnoRef.current.value;
    const pin = pinRef.current.value;

    axios
      .post("http://localhost:8000/user/login", {
        cardno,
        pin,
      })
      .then((response) => {
        makeToast("success", response.data.message);
        localStorage.setItem("U_Token", response.data.token);
        props.history.push("/servies");
      })
      .catch((err) => {
        if (
          err &&
          err.response &&
          err.response.data &&
          err.response.data.message
        ) {
          makeToast("error", err.response.data.message);
        }
      });
  };
  return (
    <>
      <div className="oueterbox">
        <div class="container">
          <div className="row"></div>

          <div className="row">
            <div className="col-sm-0.5"></div>
            <div className="col-sm-10">
              <div className="card">
                <div className="cardHeader">Sign Up</div>

                <div className="cardBody">
                  <div className="form-group">
                    <label className="form-label" htmlFor="name">
                      Name
                    </label>
                    <input
                      className="form-control"
                      type="text"
                      name="name"
                      placeholder="Your Name"
                      ref={cardnoRef}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label" htmlFor="email">
                      Your Email
                    </label>
                    <input
                      className="form-control"
                      type="email"
                      name="email"
                      placeholder="abc@example.com"
                      ref={pinRef}
                    />
                  </div>

                  <button className="subbtn" onClick={loginUser}>
                    Enter
                  </button>
                </div>
              </div>
            </div>
            <div className="col-sm-1"></div>
          </div>
        </div>
      </div>
    </>
  );
}

export default withRouter(LoginPage);
